<?phpfunction _get_lh($offset,$limit){    global $cmdb;    $lh=$cmdb->get_results($cmdb->prepare("SELECT * from $cmdb->lecture_hall LIMIT %d,%d",$offset,$limit));    return $lh;}function get_lh_to_edit($lh_id){    global $cmdb;    $lh=$cmdb->get_row($cmdb->prepare("SELECT * from $cmdb->lecture_hall WHERE lh_id=%d LIMIT 1",$lh_id));    return $lh;}function _get_num_lh(){    global $cmdb;    return $cmdb->get_var("SELECT count( ID ) FROM $cmdb->lecture_hall");}function delete_lh($lh_id){    global $cmdb;    return $cmdb->query($cmdb->prepare("DELETE FROM $cmdb->lecture_hall WHERE lh_id=%d",$lh_id));}function edit_lh_link(){    return admin_url("lecture-hall.php?action=edit&lh=".get_lh_id());    }function delete_lh_link(){    return admin_url("lecture-hall.php?action=delete&lh=".get_lh_id());    }function editlhlink(){    echo edit_lh_link();}function deletelhlink(){    echo delete_lh_link();}?>