<?phpglobal $editing,$redirect_to;    if($editing){        $head_title="Chỉnh sửa giảng đường";        $action='update';    }else{        $head_title="Thêm giảng đường mới";        $action='new';    }?><p class="title-manager"><?php echo $head_title;?></p><br /><form class="form" action="lecture-hall.php" method="POST"><label for="lh_name">Giảng đường:</label><input type="text" id="lh_name" name="lh_name" value="<?php ?>"/><label for="lh_address">Địa chỉ:</label><textarea name="lh_address" id="lh_address"><?php ?></textarea><div class="fixed"></div><br/><p align="center"><input type="submit" class="button" name="update-<?php echo $action?>" value="<?php $editing?_e("Cập nhật"):_e("Thêm giảng đường")?>"/><input type="reset" class="button" value="Nhập lại"/></p><input type="hidden" name="action" value="<?php echo $action?>"/><input type="hidden" name="lh" value="<?php lh_id()?>"/><input type="hidden" name="redirect_to" value="<?php echo $redirect_to;?>"/></form>